return {
    "topic": msg.topic,
    "payload": {
        "chatId": chatId,
        "type": "message",
        "content": msg.payload
    }
};

//Out1: Telegram Message, Out2: MQTT Send to display
var DISPLAY_SIZE = 2 * 16;

if(msg.payload.content === "/start" || msg.payload.content === "/info"){
    return [{
        "topic": "sendTelegramMessage",
        "payload": {
            "chatId": msg.payload.chatId,
            "type": "message",
            "content": "ChatID: " + msg.payload.chatId
        }
    }, null];
}

if(msg.payload.content.startsWith("/send ")){
    var text = msg.payload.content.replace("/send ", "");

    if(text.length <= DISPLAY_SIZE){
        return [{
            "topic": "sendTelegramMessage",
            "payload": {
                "chatId": msg.payload.chatId,
                "type": "message",
                "content": "Nachricht an Display geschickt."
            }
        },
        {
            "topic": "sendMQTTMessage",
            "payload": text
        }];
    }

    return [{
        "topic": "sendTelegramMessage",
            "payload": {
                "chatId": msg.payload.chatId,
                "type": "message",
                "content": "Nachricht zu lang, maximal " + DISPLAY_SIZE + " Zeichen."
            }
    }, null];

}

return null;

var lastOpenTime = context.get("lastOpenTime");
var lastCloseTime = context.get("lastCloseTime");
var minInterval = context.get("minInterval");
var now = Date.now();

if(msg.topic === "doorIntervalSwitch"){
    context.set("minInterval", msg.payload * 1000);
    return null;
}else{
    if(msg.payload === 0){
        if((now - minInterval) >= lastOpenTime){
            context.set("lastOpenTime", now);
            return msg;
        }
        return null;
    }else if(msg.payload === 1){
        if((now - minInterval) >= lastCloseTime){
            context.set("lastCloseTime", now);
            return msg;
        }
        return null;
    }
    return null;
}

